package JavaLoops;

public class ifelseexample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=15;
		int b=16;
		int c=8;
		
		if (a>b)
		{
			if(a>c)
			{
				System.out.println("A is greater");
			}
			else
			{
				System.out.println("C is greater");
			}
		}
		else
		{
			if(b>c)
			{
				System.out.println("B is greater");
			}
			else
			{
				System.out.println("C is greater");
			}
		}
	}

}
